import React, { useState, useMemo } from 'react';
import { Container, Row, Col, Card, Button, Form, Nav } from 'react-bootstrap';

const Productos = () => {
  // Datos iniciales
  const [productos] = useState([
    { id: 1, name: 'Zapatillas Superstar', price: '89.990', originalPrice: '99.990', brand: 'Originals', imageUrl: 'https://placehold.co/400x400/eeeeee/333333?text=Superstar' },
    { id: 2, name: 'Zapatillas Campus 00s', price: '99.990', originalPrice: null, brand: 'Originals', imageUrl: 'https://placehold.co/400x400/cccccc/333333?text=Campus+00s' },
    { id: 3, name: 'Zapatillas Handball Spezial', price: '99.990', originalPrice: '120.000', brand: 'Originals', imageUrl: 'https://placehold.co/400x400/dddddd/333333?text=Handball+Spezial' },
    { id: 4, name: 'Zapatillas Campus Grey', price: '99.990', originalPrice: null, brand: 'Originals', imageUrl: 'https://placehold.co/400x400/bbbbbb/333333?text=Campus+Grey' },
    { id: 5, name: 'Zapatillas Cloudfoam', price: '59.990', originalPrice: '65.000', brand: 'Casual', imageUrl: 'https://placehold.co/400x400/999999/ffffff?text=Cloudfoam' },
    { id: 6, name: 'Botines de Fútbol', price: '159.990', originalPrice: null, brand: 'Performance', imageUrl: 'https://placehold.co/400x400/888888/ffffff?text=Botines' },
    { id: 7, name: 'Mocasines Classic', price: '75.000', originalPrice: '85.000', brand: 'Casual', imageUrl: 'https://placehold.co/400x400/aaaaaa/333333?text=Mocasines' },
    { id: 8, name: 'Tenis Running Pro', price: '110.000', originalPrice: null, brand: 'Performance', imageUrl: 'https://placehold.co/400x400/777777/ffffff?text=Running+Pro' },
    { id: 9, name: 'Sandalias Comfy', price: '35.000', originalPrice: '40.000', brand: 'Casual', imageUrl: 'https://placehold.co/400x400/666666/ffffff?text=Sandalias' },
  ]);

  const [filtroCategoria, setFiltroCategoria] = useState('Todos');
  const [orden, setOrden] = useState('default');

  // Helper para convertir "89.990" en número 89990 para poder ordenar
  const limpiarPrecio = (str) => Number(str.replace(/\./g, ''));

  // Lógica de Filtrado y Ordenamiento
  const productosFiltrados = useMemo(() => {
    let resultado = [...productos];

    // 1. Filtrar por marca/categoría
    if (filtroCategoria !== 'Todos') {
      resultado = resultado.filter(p => p.brand === filtroCategoria);
    }

    // 2. Ordenar
    if (orden === 'precio-asc') {
      resultado.sort((a, b) => limpiarPrecio(a.price) - limpiarPrecio(b.price));
    } else if (orden === 'precio-desc') {
      resultado.sort((a, b) => limpiarPrecio(b.price) - limpiarPrecio(a.price));
    } else if (orden === 'alfa') {
      resultado.sort((a, b) => a.name.localeCompare(b.name));
    }

    return resultado;
  }, [productos, filtroCategoria, orden]);

  return (
    <Container fluid className="mt-4 px-4">
      <Row>
        {/* --- SIDEBAR DE FILTROS --- */}
        <Col md={3} lg={2} className="mb-4">
          <h5 className="fw-bold mb-3">Filtros</h5>
          
          <div className="mb-4">
            <h6 className="small fw-bold text-uppercase text-muted">Categoría</h6>
            <Nav className="flex-column">
              {['Todos', 'Originals', 'Casual', 'Performance'].map(cat => (
                <Nav.Link 
                  key={cat}
                  onClick={() => setFiltroCategoria(cat)}
                  className={`p-1 ps-0 ${filtroCategoria === cat ? 'fw-bold text-primary' : 'text-dark'}`}
                  style={{ cursor: 'pointer' }}
                >
                  {cat}
                </Nav.Link>
              ))}
            </Nav>
          </div>

          <div>
            <h6 className="small fw-bold text-uppercase text-muted">Ordenar por</h6>
            <Form.Select size="sm" onChange={(e) => setOrden(e.target.value)}>
              <option value="default">Relevancia</option>
              <option value="precio-asc">Menor precio</option>
              <option value="precio-desc">Mayor precio</option>
              <option value="alfa">A-Z</option>
            </Form.Select>
          </div>
        </Col>

        {/* --- GRILLA DE PRODUCTOS --- */}
        <Col md={9} lg={10}>
          <Row xs={1} md={2} lg={3} xl={4} className="g-4">
            {productosFiltrados.map((prod) => (
              <Col key={prod.id}>
                <Card className="h-100 border-0 shadow-sm card-producto">
                  <div className="position-relative bg-light text-center p-4">
                    <Card.Img variant="top" src={prod.imageUrl} style={{ mixBlendMode: 'multiply' }} />
                    <div className="position-absolute top-0 end-0 p-2">
                       <div className="bg-white rounded-circle shadow-sm d-flex align-items-center justify-content-center" style={{ width: '30px', height: '30px' }}>
                          ●
                       </div>
                    </div>
                  </div>
                  <Card.Body className="px-2 pt-3">
                    <div className="d-flex align-items-baseline gap-2">
                      <h5 className="fw-bold mb-0">${prod.price}</h5>
                      {prod.originalPrice && (
                        <span className="text-muted text-decoration-line-through small">${prod.originalPrice}</span>
                      )}
                    </div>
                    <Card.Title className="fs-6 mt-1 mb-0 fw-normal text-truncate">
                      {prod.name}
                    </Card.Title>
                    <Card.Text className="text-muted small mb-3">
                      {prod.brand}
                    </Card.Text>
                    <Button variant="dark" className="w-100 rounded-1 py-2 fw-bold small">
                      Agregar al carrito
                    </Button>
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Row>
        </Col>
      </Row>
    </Container>
  );
};

export default Productos;
