import React, { useState } from 'react';
import { Card, Button, Row, Col, Container, Carousel, Modal, Image, Badge } from 'react-bootstrap';
import { Link } from 'react-router-dom';

// --- DATOS DE EJEMPLO (Se mantienen igual) ---
const products = [
  { id: 1, name: 'Zapatillas Superstar', price: '89.990', originalPrice: '99.990', brand: 'Originals', description: 'El estilo cl치sico de las canchas de baloncesto de los 70.', imageUrl: 'https://placehold.co/400x400/eeeeee/333333?text=Superstar' },
  { id: 2, name: 'Zapatillas Campus 00s', price: '99.990', originalPrice: null, brand: 'Originals', description: 'Inspiradas en el skate de los 2000.', imageUrl: 'https://placehold.co/400x400/cccccc/333333?text=Campus+00s' },
  // ... resto de tus productos
];

const chunkArray = (arr, size) => {
  const chunkedArr = [];
  for (let i = 0; i < arr.length; i += size) {
    chunkedArr.push(arr.slice(i, i + size));
  }
  return chunkedArr;
};

// --- COMPONENTE DE TARJETA (Actualizado con onClick) ---
const ProductCard = ({ product, toggleFavorite, addToCart, isFavorite, onOpenModal }) => {
  return (
    <Card 
      className="product-card border-0 rounded-0 p-2 h-100 shadow-sm" 
      style={{ cursor: 'pointer' }}
      onClick={() => onOpenModal(product)} // Al hacer clic en la card abre el modal
    >
      <div style={{ position: 'relative' }}>
        <Card.Img variant="top" src={product.imageUrl} alt={product.name} className="rounded-0" />
        <Button
          variant="light"
          className="rounded-circle p-2 shadow-sm"
          style={{ position: 'absolute', top: '10px', right: '10px', zIndex: 10 }}
          onClick={(e) => {
            e.stopPropagation(); // Evita abrir el modal al dar a favoritos
            toggleFavorite(product);
          }}
        >
          <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill={isFavorite ? "red" : "black"} viewBox="0 0 16 16">
            <path fillRule="evenodd" d="M8 1.314C3.856 1.314 0 4.045 0 8c0 4.286 4.093 7.02 8 10.472 3.907-3.452 8-6.186 8-10.472 0-3.955-3.856-6.686-8-6.686z"/>
          </svg>
        </Button>
      </div>

      <Card.Body className="p-1 pt-3">
        <div className="d-flex align-items-center mb-1">
          <span className="fw-bold me-2" style={{ fontSize: '1.1em' }}>${product.price}</span>
          {product.originalPrice && (
            <span className="text-muted text-decoration-line-through small">${product.originalPrice}</span>
          )}
        </div>
        <Card.Title className="mb-0 text-dark text-truncate" style={{ fontSize: '0.95em' }}>
          {product.name}
        </Card.Title>
        <Card.Text className="text-muted mb-2" style={{ fontSize: '0.85em' }}>{product.brand}</Card.Text>
        <Button
          variant="dark"
          size="sm"
          className="w-100 rounded-1 fw-bold"
          onClick={(e) => {
            e.stopPropagation(); // Evita abrir el modal al agregar al carrito
            addToCart(product);
          }}
        >
          Agregar al carrito
        </Button>
      </Card.Body>
    </Card>
  );
};

// --- COMPONENTE SLIDER PRINCIPAL ---
const ProductSlider = ({ title }) => {
  const [favorites, setFavorites] = useState([]);
  const [cart, setCart] = useState([]);

  // ESTADOS PARA EL MODAL
  const [showModal, setShowModal] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);

  const handleOpenModal = (prod) => {
    setSelectedProduct(prod);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setSelectedProduct(null);
  };

  const toggleFavorite = (product) => {
    setFavorites((prev) =>
      prev.find((p) => p.id === product.id)
        ? prev.filter((p) => p.id !== product.id)
        : [...prev, product]
    );
  };

  const addToCart = (product) => {
    setCart((prev) => {
      const exists = prev.find((p) => p.id === product.id);
      if (exists) {
        return prev.map((p) =>
          p.id === product.id ? { ...p, quantity: (p.quantity || 1) + 1 } : p
        );
      }
      return [...prev, { ...product, quantity: 1 }];
    });
  };

  const cardsPerSlide = 4;
  const productSlides = chunkArray(products, cardsPerSlide);

  return (
    <Container className="my-5">
      <Row className="mb-3 align-items-center">
        <Col xs={6}><h2 className="fw-bold">{title}</h2></Col>
        <Col xs={6} className="text-end">
          <Button variant="outline-dark" className="rounded-0 px-4 py-1" as={Link} to="/productos">Ver m치s</Button>
        </Col>
      </Row>

      <Carousel indicators={false} interval={null} variant="dark">
        {productSlides.map((slide, index) => (
          <Carousel.Item key={index} className="p-2">
            <Row xs={2} md={3} lg={4} className="g-4">
              {slide.map((product) => (
                <Col key={product.id}>
                  <ProductCard
                    product={product}
                    toggleFavorite={toggleFavorite}
                    addToCart={addToCart}
                    isFavorite={favorites.some((p) => p.id === product.id)}
                    onOpenModal={handleOpenModal}
                  />
                </Col>
              ))}
            </Row>
          </Carousel.Item>
        ))}
      </Carousel>

      {/* --- MODAL DE DETALLE (Reutilizado del componente de productos) --- */}
      {selectedProduct && (
        <Modal show={showModal} onHide={handleCloseModal} size="lg" centered>
          <Modal.Header closeButton className="border-0"></Modal.Header>
          <Modal.Body className="px-4 pb-4">
            <Row className="align-items-center">
              <Col md={6} className="text-center bg-light rounded p-4">
                <Image src={selectedProduct.imageUrl} fluid className="rounded" style={{ maxHeight: '400px' }} />
              </Col>
              <Col md={6} className="ps-lg-5 mt-4 mt-md-0">
                <Badge bg="secondary" className="mb-2">{selectedProduct.brand}</Badge>
                <h2 className="fw-bold mb-2">{selectedProduct.name}</h2>
                <div className="d-flex align-items-center gap-3 mb-3">
                  <h3 className="text-primary fw-bold mb-0">${selectedProduct.price}</h3>
                  {selectedProduct.originalPrice && (
                    <span className="text-muted text-decoration-line-through">${selectedProduct.originalPrice}</span>
                  )}
                </div>
                <p className="text-secondary small mb-4">{selectedProduct.description || "Sin descripci칩n disponible."}</p>
                <div className="d-grid gap-2">
                  <Button variant="primary" size="lg" className="fw-bold">Comprar ahora</Button>
                  <Button 
                    variant="outline-dark" 
                    size="lg" 
                    onClick={() => addToCart(selectedProduct)}
                  >
                    Agregar al carrito
                  </Button>
                  <Button 
                    variant="link" 
                    className="text-danger text-decoration-none mt-2"
                    onClick={() => toggleFavorite(selectedProduct)}
                  >
                    {favorites.some(p => p.id === selectedProduct.id) ? '仇벒잺 En Favoritos' : '游밼 Agregar a Favoritos'}
                  </Button>
                </div>
              </Col>
            </Row>
          </Modal.Body>
        </Modal>
      )}
    </Container>
  );
};

export default ProductSlider;
